void writeCMD(uint8_t command)
{
 // ePaper_DC_0;
 //  ePaper_CS_0;
 // SPI.transfer(command);
 // ePaper_CS_1;

    SPI_Delay(1);
    EPD_W21_CS_0;                   
	EPD_W21_DC_0;		// command write
	SPI_Write(command);
	EPD_W21_CS_1;
}

//this function will take in a byte and send it to the display with the 
