{
//  ePaper_DC_1;
//  ePaper_CS_0;
//  SPI.transfer(data);
//  ePaper_CS_1;

    SPI_Delay(1);
    EPD_W21_CS_0;                   
	EPD_W21_DC_1;		// command write
	SPI_Write(data);
	EPD_W21_CS_1;
}

