void initEPD()
{
  int i;
  //-----------------------------------------------------------------------------
  //more detail on the following commands and additional commands not used here  
  //can be found on the CFAP104212E0-0213 datasheet on the Crystalfontz website 
  //-----------------------------------------------------------------------------

    //reset driver
  ePaper_RST_0;
   SPI_Delay(200);
  ePaper_RST_1;
   SPI_Delay(200);
  
  //Booster Soft Start
  writeCMD(0x06);
  writeData(0x17);
  writeData(0x17);
  writeData(0x17);

  //Power On
  writeCMD(0x04);
  //wait until powered on
  display_status();

  writeCMD(0x00);
  writeData(0x13);

  //Resolution
  writeCMD(0x61);
  writeData(0x68);  
  writeData(0x00); 
  writeData(0xd4);
//  writeData(HRES);  
//  writeData(VRES>>8); 
//  writeData(VRES&0xff);

  //Vcom and data interval setting
  writeCMD(0x50);
  writeData(0x67);
}


